
public class Combine_print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		 int n=4;
		 
		 for(int i=1;i<=n;i++) {
			 
			 for(int j=1;j<=i;j++) {
				 
				 System.out.print("*");
			 }
			 
			 System.out.println("");
		 }
       
			
			int n2=1;
			
			for(int i=4;i>=n2;i--) {
				for(int j=1;j<=i;j++) {
					
					System.out.print("*");
				}
				
				System.out.println("");
			}
		

	
	
	}

}
